jQuery(document).ready(function($){$(document).on('click','#people-filter div.people-filter',function(e){e.preventDefault();var url=$(this).data('url');window.location=url});if($("#people-filter").length){initPeopleFilter()}
function initPeopleFilter(){people_filter_loaded=!1;people_filter_count=$('div.people-filter').length;people_filter_loaded=0;$('div.people-filter').each(function(){var person=$(this);var background=person.data('background');var img=new Image();img.onload=function(){var width=person.outerWidth();var height=(width*this.height)/this.width;person.css('height',height).css('background-image','url('+background+')').css('background-size','contain').css('background-repeat','no-repeat').show()}
img.src=background;people_filter_loaded++});var people_filter_interval=setInterval(function(){if($('.pace').hasClass('pace-inactive')){clearInterval(people_filter_interval);var hash=window.location.hash;if(hash){hash=hash.replace('#','.');$('.js--masonry').isotope({filter:hash});$("a.js--masonry-lk[data-filter*='"+hash+"']").addClass('__active')}else{$('.js--masonry').isotope({filter:'.leadership-key-staff'});$("a.js--masonry-lk[data-filter*='.leadership-key-staff']").addClass('__active')}}},100)}})